// led signal is reversed in ESP8266 (HIGH = LED OFF).
// Any other output keeps consistent payload (HIGH = 1).
if (msg.topic == "led") {
    msg.payload = msg.payload == true? 0 
        : msg.payload == false? 1 
        : msg.payload;
}
else {
    msg.payload = msg.payload == true ? 1 
        : msg.payload == false ? 0 
        : msg.payload;
}

// Clean msg with only payload and topic
const newMsg = { 
    "payload": msg.payload, 
    "topic": msg.topic 
}
return newMsg;

// The msg received can be ot the types:
//  {"payload": 20, "topic": "thermostat"}
//  {"payload": 0.3, "topic": "deadband"}
//  {"payload": 21.4, "topic": "temp"}
// The topic is the name of the flow-scoped variable.
// The payload is the new value for that variable

// Update the received variable ("deadband" / "temp" / "thermostat") in the flow scope: :
flow.set(msg.topic, msg.payload);

// Calculate the heater output and assign it to msg.payload
msg.payload = flow.get("thermostat") > flow.get("temp") + flow.get("deadband") ? 1 : 0;

// the result is stored in fow-scoped variable "heater"
// No output if heater" condition does not change.
if (flow.get("heater") == msg.payload) { return };

// If "heater" state changes send msg with topic="heater" and correspondign payload.
flow.set("heater", msg.payload);
msg.topic = "heater";

return msg;

msg._session = "";
if (!!msg.topic) { return [msg, null]; } 
else { return [null, msg]; }

switch (msg.topic) {
    case "interval":  
        return [msg, null, null, null, null, null, null];
        break;
    case "heater": 
        return [null, msg, null, null, null, null, null];
        break;
    case "boiler":
        return [null, null, msg, null, null, null, null];
        break;
    case "debug":
        return [null, null, null, msg, null, null, null];
        break;    
    case "led":
        return [null, null, null, null, msg, null, null];
        break;
    case "espIP":
        return [null, null, null, null, null, msg, null];
        break;
    default:
        msg.payload = "ws failed";
        return [null, null, null, null, null, null, msg];
}

const interfaces = os.networkInterfaces();
let localIP = '';

const preferredInterfaces = ['Wi-Fi', 'wlan0', 'eth0', 'Ethernet'];

for (const name of preferredInterfaces.concat(Object.keys(interfaces))) {
    const ifaceList = interfaces[name];
    if (!ifaceList) continue;

    for (const iface of ifaceList) {
        if (iface.family === 'IPv4' && !iface.internal) {
            localIP = iface.address;
            break;
        }
    }
    if (localIP) break;
}

msg.topic = "localIP";
msg.payload = localIP || "Not found";
return msg;
