// led signal is reversed in ESP8266 (HIGH = LED OFF).
// Any other output keeps consistent payload (HIGH = 1).
if (msg.topic == "led") {
    msg.payload = msg.payload == true? 0 
        : msg.payload == false? 1 
        : msg.payload;
}
else {
    msg.payload = msg.payload == true ? 1 
        : msg.payload == false ? 0 
        : msg.payload;
}

// Clean msg with only payload and topic
return { payload: msg.payload, topic: msg.topic }

// The msg received can be ot the types:
//  {"payload": 20, "topic": "thermostat"}
//  {"payload": 0.3, "topic": "deadband"}
//  {"payload": 21.4, "topic": "temp"}
// The topic is the name of the flow-scoped variable.
// The payload is the new value for that variable

// Update the received variable ("deadband" / "temp" / "thermostat") in the context scope: :
context.set(msg.topic, msg.payload);

// Calculate the heater output and assign it to msg.payload
const T = context.get("temp"),
    DB = context.get("deadband"),
    Setpoint = context.get("thermostat"),
    Heater = Setpoint > T + DB ? 1 : 0;

// No output if heater" condition does not change.
if (context.get("heater") == Heater) { return };

// If "heater" state changes update context and msg; 
// Send msg with topic="heater" and correspondign payload.
context.set("heater", Heater);
return { payload: Heater, topic: "heater"}

msg._session = "";
if (!!msg.topic) { return [msg, null]; } 
else { return [null, msg]; }

const topicMap = {
    interval: 0,
    heater: 1,
    boiler: 2,
    debug: 3,
    led: 4,
    espIP: 5,
    wifi: 6,
    "": 7
};

let result = Array(8).fill(null);
if (topicMap.hasOwnProperty(msg.topic)) {
    result[topicMap[msg.topic]] = msg;
}
return result;




