// Get all the network interfaces on the system:
const interfaces = os.networkInterfaces();
let localIP = '';

const preferredInterfaces = ['Wi-Fi', 'wlan0', 'eth0', 'Ethernet'];

for (const name of preferredInterfaces.concat(Object.keys(interfaces))) {
    const ifaceList = interfaces[name];
    if (!ifaceList) continue;

    for (const iface of ifaceList) {
        if (iface.family === 'IPv4' && !iface.internal) {
            localIP = iface.address;
            break;
        }
    }
    if (localIP) break;
}

msg.topic = "localIP";
msg.payload = localIP || "Not found";
return msg;

let msgHum = null, 
    msgPres = null,
    msgSLP = null,
    msgTemp = null, 
    msgTime = null;

// Update variables with topic in context (elevation):
if (!!msg.topic) { context.set(msg.topic, msg.payload); }

// Update BME readings (msg received with no topic):
else { 
    msgTemp = { payload: msg.temp / 10, topic: "temp" };
    msgHum = { payload: msg.hum / 10};
    msgPres = {payload: msg.pres / 10};
    msgTime = { payload: new Date().toLocaleString("es-ES") };

    context.set("temp", msg.temp / 10);
    context.set("pres", msg.pres / 10);
}

let T = context.get("temp");
const P = context.get("pres");
const H = context.get("elevation");

if (T === undefined || P === undefined || H === undefined) {
    node.warn("Missing required inputs. Waiting for all inputs to be available.");
    return null; // Don't compute until all data is available
}

// Calculation of Sea Level Pressure:
// T = Temperature at station(in K — Kelvin)
// L = Temperature lapse rate ≈ 0.0065 K / m
// g = Gravitational acceleration ≈ 9.80665 m / s²
// M = Molar mass of dry air ≈ 0.0289644 kg / mol
// R = Universal gas constant ≈ 8.31432 N·m / (mol·K)
// exp = -(g * M)/(R * L)

T += 273.15;
const L = 0.0065,
    exp = -5.257;

msgSLP = { payload: Math.round(P * Math.pow(T / (T + (L * H)), exp)) }

return [msgHum, msgSLP, msgPres, msgTime, msgTemp];
